const path = require('path')
const fs = require('fs')

class Test {
  constructor() {
    const customConfigPath = path.join(require.main.path, 'test.config.js')
    const isConfigExist = fs.existsSync(customConfigPath)
    if (isConfigExist) {
      this._config = require(customConfigPath)
    }
  }

  get config() {
    return this._config || {
      isDefault: true
    }
  }
}

module.exports = new Test()